const drafts = $('Content Draft laden').all().map(i => i.json);
const meta = $('Meta & Links laden').all().map(i => i.json);
const projekt = $('Projekt-Daten laden').first().json;

const titleSuggestion = meta.find(m => m.typ === 'title_tag')?.vorschlag || projekt.thema;

let markdown = `# ${titleSuggestion}\n\n`;
for (const draft of drafts) {
  const content = draft.content_humanized || draft.content_raw;
  markdown += content + '\n\n';
}

// Add internal link suggestions as comments
const links = meta.filter(m => m.typ?.includes('link'));
if (links.length > 0) {
  markdown += '\n\n---\n## Interne Link-Vorschl√§ge\n\n';
  for (const link of links) {
    markdown += `- ${link.vorschlag} ‚Üí ${link.ziel_url || ''}\n`;
  }
}

return [{ json: { markdown, filename: `artikel.md` } }];

const drafts = $('Content Draft laden').all().map(i => i.json);
const meta = $('Meta & Links laden').all().map(i => i.json);
const projekt = $('Projekt-Daten laden').first().json;

const title = meta.find(m => m.typ === 'title_tag')?.vorschlag || projekt.thema;
const metaDesc = meta.find(m => m.typ === 'meta_description')?.vorschlag || '';

let body = '';
for (const draft of drafts) {
  const content = draft.content_humanized || draft.content_raw;
  // Basic markdown to HTML conversion
  const html = content
    .replace(/^### (.*$)/gm, '<h3>$1</h3>')
    .replace(/^## (.*$)/gm, '<h2>$1</h2>')
    .replace(/^# (.*$)/gm, '<h1>$1</h1>')
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/g, '<em>$1</em>')
    .replace(/\n\n/g, '</p><p>')
    .replace(/^(.+)$/gm, '<p>$1</p>');
  body += html + '\n';
}

const htmlDoc = `<!DOCTYPE html>
<html lang="de">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>${title}</title>
  <meta name="description" content="${metaDesc}">
</head>
<body>
  <article>
    ${body}
  </article>
</body>
</html>`;

return [{ json: { html: htmlDoc, filename: 'artikel.html' } }];

const qa = $('QA-Ergebnisse laden').all().map(i => i.json);
const meta = $('Meta & Links laden').all().map(i => i.json);
const projekt = $('Projekt-Daten laden').first().json;

let bericht = `# Analyse-Bericht: ${projekt.thema}\n\n`;
bericht += `**Projekt-ID:** ${projekt.projekt_id}\n`;
bericht += `**Erstellt:** ${projekt.erstellt_am}\n\n`;

bericht += '## QA-Ergebnisse\n\n';
for (const q of qa) {
  const icon = q.status === 'bestanden' ? '‚úÖ' : '‚ö†Ô∏è';
  bericht += `${icon} **${q.check_typ}**: ${q.details} (Score: ${q.score})\n`;
}

bericht += '\n## Meta-Vorschl√§ge\n\n';
const titles = meta.filter(m => m.typ === 'title_tag');
const descs = meta.filter(m => m.typ === 'meta_description');
bericht += '### Title Tags\n';
titles.forEach((t, i) => { bericht += `${i+1}. ${t.vorschlag}\n`; });
bericht += '\n### Meta Descriptions\n';
descs.forEach((d, i) => { bericht += `${i+1}. ${d.vorschlag}\n`; });

bericht += '\n## Bild-Ideen & Midjourney-Prompts\n\n';
const visuals = meta.filter(m => m.typ === 'bild_idee' || m.typ === 'midjourney_prompt');
visuals.forEach(v => { bericht += `- **${v.typ}**: ${v.vorschlag}\n`; });

bericht += '\n## Link-Strategie\n\n';
const links = meta.filter(m => m.typ?.includes('link'));
links.forEach(l => { bericht += `- ${l.vorschlag} ‚Üí ${l.ziel_url || 'N/A'}\n`; });

return [{ json: { bericht, filename: 'analyse-bericht.md' } }];

const meta = $('Meta & Links laden').all().map(i => i.json);
const projekt = $('Projekt-Daten laden').first().json;

const titles = meta.filter(m => m.typ === 'title_tag');
const descs = meta.filter(m => m.typ === 'meta_description');

let content = `# Meta-Vorschl√§ge: ${projekt.thema}\n\n`;

content += '## Title Tag Vorschl√§ge\n\n';
titles.forEach((t, i) => {
  content += `### Option ${i + 1}\n`;
  content += `- **Vorschlag:** ${t.vorschlag}\n`;
  content += `- **Zeichenl√§nge:** ${t.vorschlag?.length || 0}\n\n`;
});

content += '## Meta Description Vorschl√§ge\n\n';
descs.forEach((d, i) => {
  content += `### Option ${i + 1}\n`;
  content += `- **Vorschlag:** ${d.vorschlag}\n`;
  content += `- **Zeichenl√§nge:** ${d.vorschlag?.length || 0}\n\n`;
});

return [{ json: { content, filename: 'meta-vorschlaege.md' } }];

const meta = $('Meta & Links laden').all().map(i => i.json);
const projekt = $('Projekt-Daten laden').first().json;

const internalLinks = meta.filter(m => m.typ === 'interner_link');
const externalLinks = meta.filter(m => m.typ === 'externer_link');
const allLinks = meta.filter(m => m.typ?.includes('link'));

let content = `# Link-Strategie: ${projekt.thema}\n\n`;

content += '## Interne Verlinkung\n\n';
if (internalLinks.length > 0) {
  internalLinks.forEach((l, i) => {
    content += `${i + 1}. **${l.vorschlag}**\n`;
    content += `   - Ziel-URL: ${l.ziel_url || 'N/A'}\n`;
    content += `   - Ankertext-Vorschlag: ${l.ankertext || l.vorschlag}\n\n`;
  });
} else {
  allLinks.forEach((l, i) => {
    content += `${i + 1}. **${l.vorschlag}**\n`;
    content += `   - Ziel-URL: ${l.ziel_url || 'N/A'}\n\n`;
  });
}

content += '## Externe Verlinkung\n\n';
if (externalLinks.length > 0) {
  externalLinks.forEach((l, i) => {
    content += `${i + 1}. **${l.vorschlag}**\n`;
    content += `   - Ziel-URL: ${l.ziel_url || 'N/A'}\n\n`;
  });
} else {
  content += '_Keine externen Links vorgeschlagen._\n';
}

return [{ json: { content, filename: 'link-strategie.md' } }];

const meta = $('Meta & Links laden').all().map(i => i.json);
const projekt = $('Projekt-Daten laden').first().json;

const bildIdeen = meta.filter(m => m.typ === 'bild_idee');
const mjPrompts = meta.filter(m => m.typ === 'midjourney_prompt');

let content = `# Visual-Plan: ${projekt.thema}\n\n`;

content += '## Bild-Ideen\n\n';
if (bildIdeen.length > 0) {
  bildIdeen.forEach((b, i) => {
    content += `### Bild ${i + 1}\n`;
    content += `- **Beschreibung:** ${b.vorschlag}\n`;
    content += `- **Position im Artikel:** ${b.position || 'N/A'}\n\n`;
  });
} else {
  content += '_Keine Bild-Ideen vorhanden._\n\n';
}

content += '## Midjourney Prompts\n\n';
if (mjPrompts.length > 0) {
  mjPrompts.forEach((p, i) => {
    content += `### Prompt ${i + 1}\n`;
    content += '```\n';
    content += `${p.vorschlag}\n`;
    content += '```\n\n';
  });
} else {
  content += '_Keine Midjourney-Prompts vorhanden._\n\n';
}

content += '## Verwendungshinweise\n\n';
content += '- Alle Bilder sollten mit passendem Alt-Text versehen werden\n';
content += '- Bildformat: WebP bevorzugt, max. 200KB\n';
content += '- Midjourney-Parameter: --ar 16:9 --v 6 empfohlen\n';

return [{ json: { content, filename: 'visual-plan.md' } }];
